     then fn:exists($B)
else if (fn:deep-equal($A[1], $B[1], $C)) 
     then deep-less-than(fn:tail($A), fn:tail($B), $C)
else if ($A[1] ne $A[1] (:that is, $A[1] is NaN:)) 
     then fn:true()
else if (is-string($A[1]) and is-string($B[1]) 
     then fn:compare($A[1], $B[1], $C) lt 0
else $A[1] lt $B[1]

